// This sample code reads in image data from a RAW image file and // writes it into another file// NOTE:	The code assumes that the image is of size 256 x 256 and is in the//			RAW format. You will need to make corresponding changes to//			accommodate images of different sizes and/or types#include <stdio.h>#include <iostream>#include <stdlib.h>using namespace std;int main(int argc, char *argv[]){	// Define file pointer and variables	FILE *file;	int BytesPerPixel;	int Size1;	int Size2;	float sepia[3][3] = { { 0.393,0.769,0.189 },{ 0.349,0.686,0.168 },{ 0.272,0.534,0.131 } };	float sum[3] = { 0,0,0 };	int temp;	// Check for proper syntax	if (argc < 3) {		cout << "Syntax Error - Incorrect Parameter Usage:" << endl;		cout << "program_name input_image.raw output_image1.raw output_image2.raw [BytesPerPixel] [Size1] [Size2]" << endl;		return 0;	}	// Check if image is grayscale or color	if (argc < 4) {		BytesPerPixel = 1; // default is grey image	}	else {		BytesPerPixel = atoi(argv[4]);		// Check if size is specified		if (argc >= 5) {			Size1 = atoi(argv[5]);			Size2 = atoi(argv[6]);		}	}	// Allocate image data array	unsigned char Imagedata[Size1][Size2][BytesPerPixel];	// Read image (filename specified by first argument) into image data matrix	if (!(file = fopen(argv[1], "rb"))) {		cout << "Cannot open file: " << argv[1] << endl;		exit(1);	}	fread(Imagedata, sizeof(unsigned char), Size1*Size2*BytesPerPixel, file);	fclose(file);	///////////////////////// INSERT YOUR PROCESSING CODE HERE /////////////////////////	unsigned char grayimage[Size1][Size2];	//unsigned char SepiaImage[Size1][Size2][BytesPerPixel];	for (int i = 0; i < Size1; i++) {		for (int j = 0; j < Size2; j++) {			grayimage[i][j] = 0.21*Imagedata[i][j][0] + 0.72*Imagedata[i][j][1] + 0.07*Imagedata[i][j][2];		}	}	for (int i = 0; i < 3; i++) {		for (int j = 0; j < 3; j++) {			sum[i] += sepia[i][j];		}	}	for (int i = 0; i < Size1; i++) {		for (int j = 0; j < Size2; j++) {			for (int k = 0; k < 3; k++) {				temp = sum[k] * (int)grayimage[i][j];				if (temp > 255) {					Imagedata[i][j][k] = 255;				}				else {					Imagedata[i][j][k] = sum[k] * grayimage[i][j];				}			}		}	}	// Write image data (filename specified by second argument) from image data matrix	if (!(file = fopen(argv[2], "wb"))) {		cout << "Cannot open file: " << argv[2] << endl;		exit(1);	}	fwrite(grayimage, sizeof(unsigned char), Size1*Size2, file);	fclose(file);	if (!(file = fopen(argv[3], "wb"))) {		cout << "Cannot open file: " << argv[2] << endl;		exit(1);	}	fwrite(Imagedata, sizeof(unsigned char), Size1*Size2*BytesPerPixel, file);	fclose(file);	return 0;}